import React, { useState, useEffect } from "react";

const HomePage = () => {
  const [quote, setQuote] = useState("");  // 명언을 저장할 상태
  const [isOpen, setIsOpen] = useState(false);  // 드롭다운 메뉴의 상태

  const openDropdown = () => setIsOpen(true);  // 드롭다운 메뉴를 여는 함수
  const closeDropdown = () => setIsOpen(false);  // 드롭다운 메뉴를 닫는 함수

  useEffect(() => {
    fetch(`${process.env.PUBLIC_URL}/WiseSayings.txt`)
      .then(response => response.text())
      .then(data => {
        const lines = data.split('\n');
        const randomIndex = Math.floor(Math.random() * lines.length);
        const randomLine = lines[randomIndex];
        setQuote(randomLine);
      });
  }, []);  

  return (
    <div style={{ backgroundImage: `url(${process.env.PUBLIC_URL}/images/HomePage2.jpg)`, backgroundSize: 'cover', height: '100vh' }}>
      <div style={{color: '#ffffff', fontFamily: 'bookkB', display: 'flex', justifyContent: 'center', alignItems: 'center', fontSize:'30px'}}>
        <div style={{marginTop:'20px'}}>음악을 사랑하시나요?</div>
      </div>
      <div style={{color:'white', fontFamily:'bookkB', fontSize:'20px'}}>
        <div style={{marginLeft:'80px',marginTop:'80px'}}>
          {quote}
        </div>
      </div>
      <div style={{color:'white', fontFamiliy:'bookkB', fontSize:'20px'}}>
        <div style={{marginLeft:'100px',marginTop:'50px'}}>
          <button onMouseEnter={openDropdown} onMouseLeave={closeDropdown} style={{color:'black', fontFamiliy:'bookkB', fontSize:'20px'}}></button>
          {isOpen && (
            <ul style={{color:'white', fontFamiliy:'bookkB', fontSize:'20px'}} onMouseEnter={openDropdown} onMouseLeave={closeDropdown}>
              <li>Google</li>
              <li>Youtube</li>
              <li>Naver</li>
            </ul>
          )}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
